import React, { useRef } from 'react';
import Gravatar from '@krosben/react-native-gravatar';
import MapView, { Marker } from 'react-native-maps'

import { Body, Container, MapMarker, MapMarkerContainer, MapMarkerImage, MapMarkerTitle, ModalBody, ModalContainer, ModalHeader, OverlayAddPoint,  SpottyValue, SpottyLabel } from './styles';

import Icon from 'react-native-vector-icons/Feather'
import { Alert, Dimensions, Image, Keyboard, SafeAreaView, TouchableOpacity, View } from 'react-native';
import * as Location from 'expo-location'
import Carousel from 'react-native-snap-carousel';
import { useEffect, useState } from 'react';
import Modal from '../../components/Modal';
import { useAuth } from '../../hooks/authHook';
import { useApi } from '../../hooks/customHooks';
import Header from '../../components/Header';
import { useCallback } from 'react';

const SLIDER_WIDTH = Dimensions.get('window').width
const ITEM_WIDTH = Math.round(SLIDER_WIDTH * 0.6)
const mock = [
    {
        id: 1,
        latitude: -22.908857168874896,
        longitude: -43.2769856415689,
        title: "Galpão do Engenhão",
        url: "data:image/jpeg;base64,"
    },
    {
        id: 2,
        latitude: -22.907898553139937,
        longitude: -43.28392485156655,
        title: "Praça DUO",
        url: "data:image/jpeg;base64,"
    },
    {
        id: 3,
        latitude: -22.90429131023199,
        longitude: -43.293060790747404,
        title: "Parque de Madureira",
        url: "data:image/jpeg;base64,"
    }
]
const Main: React.FC = () => {
    const {user} = useAuth()
    const {http} = useApi()
    const isCarousel = useRef(null)
    const [initialPosition, setInitialPosition] = useState<[number, number]>([0, 0])
    const [marker, setMarker] = useState<[number, number]>([0, 0])
    const [showKeyboard, setShowKeyboard] = useState(false)
    const [locationPerm, setlocationPerm] = useState(false)
    const [newSpot, setNewSpot] = useState(false)

    Keyboard.addListener('keyboardDidShow', () => {
        setShowKeyboard(true)
    })

    Keyboard.addListener('keyboardDidHide', () => {
        setShowKeyboard(false)
    })

    useEffect(() => {
        async function getPermission() {
            await Location.requestForegroundPermissionsAsync();
            const { granted } = await Location.getForegroundPermissionsAsync()

            if (!granted) {
                Alert.alert('Oooops...', 'Precisamos de sua permissão para obter a localização')
                return;
            }
            setlocationPerm(granted)
        }
        getPermission()
    }, [])

    useEffect(() => {
        const loadLocal = async () => {
            const location = await Location.getLastKnownPositionAsync({})
            //const location = await Location.getCurrentPositionAsync()
            if (!location) {
                Alert.alert('Oooops...', 'Precisamos de sua localização')
                return;
            }
            setInitialPosition([location?.coords.latitude, location?.coords.longitude])
            setMarker([location?.coords.latitude, location?.coords.longitude])
        }
        loadLocal()
    }, [])

    const handleNewSpot = useCallback((latitude:number,longitude:number)=>{
        setMarker([latitude,longitude])
    },[])


    return (
        <SafeAreaView>
            <Container>
                <Header isOpen={showKeyboard}/>
                <Body>
                    {initialPosition[0] !== 0 && (
                        <MapView style={{ width: '100%', height: '100%' }}
                            //loadingEnabled={initialPosition[0] === 0}
                            initialRegion={{
                                latitude: initialPosition[0],
                                longitude: initialPosition[1],
                                latitudeDelta: 0.014,
                                longitudeDelta: 0.014
                            }}
                            onRegionChange={(e) => setMarker([e.latitude, e.longitude])}
                            onRegionChangeComplete={(e) => setMarker([e.latitude, e.longitude])}
                        >
                            {
                                newSpot && (
                                    <Marker draggable
                                        coordinate={{
                                            latitude: marker[0],
                                            longitude: marker[1],
                                        }}
                                    />
                                )
                            }

                            {
                                mock.map(e => {
                                    return (
                                        <MapMarker
                                            coordinate={{
                                                latitude: e.latitude,
                                                longitude: e.longitude,
                                            }}
                                            key={e.id}
                                            onPress={() => {} }
                                        >
                                            <MapMarkerContainer>
                                                <MapMarkerImage source={{ uri: e.url }} style={{ resizeMode: 'cover' }} />
                                                <MapMarkerTitle>{e.title}</MapMarkerTitle>
                                            </MapMarkerContainer>
                                        </MapMarker>
                                    )
                                })
                            }

                        </MapView>
                    )}

                    {
                        !showKeyboard && (
                            <OverlayAddPoint>
                                <TouchableOpacity
                                    style={{ width: '100%', height: '100%', justifyContent: 'center', alignItems: 'center' }}
                                    onPress={() => setNewSpot(st => !st)}
                                >
                                    <Icon name={newSpot ? 'x' : 'plus'} color="white" size={35} />
                                </TouchableOpacity>
                            </OverlayAddPoint>
                        )
                    }
                </Body>
                {SpottyView(isCarousel,mock.map(e => e.url),false)}
            </Container>

        </SafeAreaView>
    );
}

const SpottyView = (ref:any,data:String[],visible:boolean) => {
    const CarouselCardItem = ({ item, index }: any) => {
        return (
            <View style={{ width: '100%', height: '100%' }} key={index}>
                <Image
                    source={{ uri: item }}
                    style={{ width: '100%', height: '100%', borderRadius: 20,resizeMode:'cover' }}
                />
            </View>
        )
    }
    return (
        <>
            <Modal
                visible={visible}
            >
                <ModalContainer>
                    <ModalHeader style={{ width: '100%', height: '10%', justifyContent: 'center', alignItems: 'flex-end', paddingRight: '5%' }}>
                        <Icon name='x' color="white" size={30} />
                    </ModalHeader>
                    <View style={{ width: '100%', height: '30%', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
                        <Carousel
                            layout="default"
                            layoutCardOffset={9}
                            ref={ref}
                            data={data}
                            renderItem={CarouselCardItem}
                            sliderWidth={SLIDER_WIDTH}
                            itemWidth={ITEM_WIDTH}
                            inactiveSlideShift={0}
                            useScrollView={true}
                        />
                    </View>
                    <ModalBody>
                        <View style={{flexDirection:"row",alignItems:'center',justifyContent:'space-evenly'}}>
                            <SpottyLabel>Nome :</SpottyLabel>
                            <SpottyValue>Galpão do Engenhão</SpottyValue>
                        </View>
                        <View style={{flexDirection:'column',paddingHorizontal:'11.5%',marginTop:10}}>
                            <SpottyLabel>Descrição :</SpottyLabel>
                            <SpottyValue style={{marginTop:10}}>
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Doloremque, quasi voluptate.
                            </SpottyValue>
                        </View>

                    </ModalBody>
                </ModalContainer>
            </Modal>
        </>
    )
}

export default Main;